const cheerio = require("cheerio")
const axios = require("axios")

function pinterest(text){
return new Promise(async(resolve,reject) => {
axios.get('https://br.pinterest.com/search/pins/?autologin=true&q=' + text, {
headers: {
"cookie" : "_auth=1; _b=\"AXYngmevd1FOfL9+KrXNr/iSdyHyhi6VWtCocTKQrIONSkGBa2Q1Ak71FEJPDpBva4Q=\"; _pinterest_; _ir=0"
}
}).then(({ data }) => {
const $ = cheerio.load(data)
const result = [];
const hasil = [];
$('div > a').get().map(b => {
const link = $(b).find('img').attr('src')
result.push(link)
});
result.forEach(v => {
if(v == undefined) return
hasil.push(v.replace(/236/g,'736'))
})
hasil.shift();
resolve(hasil)
})
})
}

module.exports.pinterest = pinterest